class Solution {

    /**
     * @param Integer[] $num
     * @param Integer $k
     * @return Integer[]
     */
    function addToArrayForm($num, $k) {
        $res = [];
        for ($i = count($num) -1; $i >=0; $i--) {
            $remainder = $k % 10;
            $addVal = $remainder + $num[$i];

            if ($addVal >= 10) {
                $addVal = $addVal % 10;
                $k = floor($k /10) + 1;
            }else {
                $k = floor($k /10);
            }
            $res[] = $addVal;
        }
        while($k) {
            $res[] = $k % 10;
            $k = floor($k /10);
        }
        return array_reverse($res);
    }
}
```

