<?php

class Solution {

    /**
     * @param String $s
     * @param String $c
     * @return Integer[]
     */
    function shortestToChar($s, $c) {
        $ans = [];
        $len = mb_strlen($s);

        // è®¡ç®—å·¦è¾¹
        $prev = PHP_INT_MIN / 2;
        for ($i = 0; $i < $len; $i++) {
            if ($s[$i] == $c) {
                $prev = $i;
            }

            $ans[] = $i - $prev;
        }

        // è®¡ç®—å³è¾¹
        $prev = PHP_INT_MAX / 2;
        for ($j = $len - 1; $j >= 0; $j--) {
            if ($s[$j] == $c) {
                $prev = $j;
            }

            $ans[$j] = min($ans[$j], $prev - $j);
        }

        return $ans;
    }
}
```


