<?php

class CustomStack {
    public $maxSize;
    public $stack = [];
    /**
     * @param Integer $maxSize
     */
    function __construct($maxSize) {
        $this->maxSize = $maxSize;
    }

    /**
     * @param Integer $x
     * @return NULL
     */
    function push($x) {
        if  (count($this->stack) < $this->maxSize) {
            array_push($this->stack, $x);
        }

        return null;
    }

    /**
     * @return Integer
     */
    function pop() {
        if (count($this->stack) <= 0) {
            return -1;
        }

        return array_pop($this->stack);
    }

    /**
     * @param Integer $k
     * @param Integer $val
     * @return NULL
     */
    function increment($k, $val) {
        for ($i = 0; $i < $k && $i < count($this->stack); $i++) {
            $this->stack[$i] += $val;
        }

        return null;
    }
}

