class Solution {

    /**
     * @param String $s
     * @return String
     */
    function decodeString($s) {
        $stack = [];
        for ($i = 0; $i < mb_strlen($s); $i++) {
             if ($s[$i] === "]") {
                 $repeatStr = "";
                 $repeatCount = "";

                 while ($stack && end($stack) !== "[") {
                     $repeatStr = array_pop($stack).$repeatStr;
                 }

                 // å»æ‰[
                 array_pop($stack);

                 while ($stack && is_numeric(end($stack))) {
                     $repeatCount = array_pop($stack).$repeatCount;
                 }

                 $repeatStr = str_repeat($repeatStr, (int)$repeatCount);
                 array_push($stack, $repeatStr);
            } else {
                 array_push($stack, $s[$i]); // åŠ å…¥æ ˆä¸­
             }
        }
        return implode('', $stack);
    }
}
