class CustomStack {
    private int stack[];
    private int top;
    private int diff[];
    public CustomStack(int maxSize) {
        this.stack = new int[maxSize];
        this.top = -1;
        this.diff = new int[maxSize];
    }
    
    public void push(int x) {
        if(top < stack.length - 1){
            stack[++top] = x;
        }
    }
    
    public int pop() {
        if(top > -1){
            return stack[top--];
        }
        return -1;
    }
    
    public void increment(int k, int val) {
        diff[0] = stack[0];
        for(int i = 1;i <= top;i++){
            diff[i] = stack[i] - stack[i-1];
        }
        if(k > top+1){
            diff[0] += val;
        }else{
        diff[0] += val;
        diff[k] -= val;
        }

        stack[0] = diff[0];
        for(int i = 1;i <= top;i++){
            stack[i] = diff[i] + stack[i-1];
        }
    }
}
