public class ClockIn20220401 {
    public static void main(String[] args) {

    }

    public static void swap(int[] nums, int i, int j) {
        if (i != j) {
            nums[i] = nums[i] ^ nums[j];
            nums[j] = nums[i] ^ nums[j];
            nums[i] = nums[i] ^ nums[j];
        }
    }
    public static int[] solution(int[] nums, int k) {
        int upper = 0;
        int exp = 1;
        int n = nums.length;
        int kLeng = 0;
        // å…ˆæ‰¾æœ€å¤§çš„èŒƒå›´
        for (int e = 1; k / e > 0; e *= 10) {
            kLeng ++;
        }
        int maxLeng = Math.max(n , kLeng);
        int[] result = new int[maxLeng + 1];
        int i = 0;
        int j = n-1;
        while (i < n / 2) {
            swap(nums, i , j);
            i ++;
            j --;
        }
        i = 0;
        while (i < maxLeng || upper == 1) {
            int left = i < n ? nums[i] : 0;
            int right = k / exp > 0 ? (k / exp) % 10 : 0;
            result[i] = (left + right + upper) % 10;
            upper = left + right + upper >= 10 ? 1 : 0;
            exp *= 10;
            i ++;
        }
        int[] ans = new int[i];
        int p = 0;
        for (int q = i - 1; q >= 0; q--) {
            ans[p ++] = result[q];
        }
        return ans;
    }
}

