class Solution {
    public int[] shortestToChar(String s, char c) {
        int len = s.length();
        int[] result = new int[len];
        int max = len - 1;

        int cur = max;
        for(int i = 0; i < len; i++) {
            if (s.charAt(i) == c) {
                result[i] = 0;
                cur = 0;
            } else {
                result[i] = Math.min(max, ++cur);
            }
        }

        cur = max;
        for(int i = len - 1; i >= 0 ; i--) {
            if (s.charAt(i) == c) {
                cur = 0;
            } else {
                result[i] = Math.min(result[i], ++cur);
            }
        }

        return result;
    }
}

