class CustomStack {
public:
    CustomStack(int maxSize) : maxSize_(maxSize), top_(-1) {
        stack_.resize(maxSize);
        inc_.resize(maxSize);
    }

    void push(int x) {
        if (top_ < maxSize_ - 1) stack_[++top_] = x;
    }

    int pop() {
        if (top_ == -1) return -1;
        if (top_ > 0) inc_[top_ - 1] += inc_[top_];
        int val = stack_[top_] + inc_[top_];
        inc_[top_--] = 0;
        return val;
    }

    void increment(int k, int val) {
        int idx = min(k - 1, top_);
        if (idx >= 0) inc_[idx] += val;
    }
private:
    vector<int> stack_;
    vector<int> inc_;
    int maxSize_;
    int top_;
};

