class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:

        c_pos, ans = [], []
        for idx, i in enumerate(s):
            if i == c:
                c_pos.append(idx)

        p = 0
        for i in range(len(s)):
            if i < c_pos[0]:
                ans.append(c_pos[0] - i)
            elif i > c_pos[-1]:
                ans.append(i - c_pos[-1])
            elif i == c_pos[p]:
                ans.append(0)
                p += 1
            else:
                ans.append(min(c_pos[p] - i, i - c_pos[p-1]))
        return ans

```

