class Solution {
    //åŒæ—¶æ­£å‘å’Œåå‘éå†
    public int[] shortestToChar(String s, char c) {
        int[] result = new int[s.length()];
        for(int i=0;i<s.length();i++){
            int head = i;
            int tail = i;
            while(true){
                if(head>=0 && s.charAt(head) == c) break;
                if(tail<s.length() && s.charAt(tail) == c) break;
                head--;
                tail++;
            }
            result[i] = (i-head)<(tail-i)?(i-head):(tail-i);
        }
        return result; 

    }
}
