class CustomStack {
    int[] mStack;
    int top = -1;
    public CustomStack(int maxSize) {
        mStack = new int[maxSize];
    }

    public void push(int x) {
        if(top < mStack.length-1){
            mStack[++top] = x;
        }

    }

    public int pop() {
        if(top>=0){
            return mStack[top--];
        }
        return -1;
    }

