    def shortestToChar(self, s, c):
        """
        :type s: str
        :type c: str
        :rtype: List[int]
        """
        
        result = [float('inf')] * len(s); 
        target = -len(s);
        for i in range(len(s)):
            if s[i] == c:
                target = i; 
            result[i] = min(result[i], abs(target-i)); 
        for i in range(len(s)-1, -1,-1):
            if s[i] == c:
                target = i; 
            result[i] = min(result[i], abs(target-i));
        return result;
