class Solution {
    public int[] shortestToChar(String s, char c) {
        //å­˜å‚¨ç»“æœ
        int[] result = new int[s.length()];
        //åˆ¤æ–­æ»‘åŠ¨çª—å£
        int head = s.indexOf(c);
        int tail = s.indexOf(c, head + 1);
        //è¯´æ˜æ²¡æœ‰çª—å£ï¼Œç›´æ¥å¡«å……ä¸¤è¾¹å€¼å³å¯
        if(tail==-1){
            int absLength = 0;
            int lP = head;
            int rP = head;
            //ä»å”¯ä¸€çš„ä¸‹æ ‡å¼€å§‹å¾€ä¸¤è¾¹æ‰©å±•
            while(lP>=0||rP<s.length()){
                if(lP>=0){
                    result[lP]=absLength;
                    lP--;
                }
                if(rP<s.length()){
                    result[rP]=absLength;
                    rP++;
                }
                absLength++;
            }
            return result;

        }
        char[] chars = s.toCharArray();

        for (int i = 0; i < chars.length; i++) {
            result[i] = Math.min(Math.abs(head - i), Math.abs(tail - i));
            //éå†åˆ°äº†å°¾å·´ï¼Œå°¾å·´å˜å¤´ï¼Œæ‰¾ä¸‹ä¸€ä¸ªå°¾å·´ã€‚
            if (i == tail) {
                head = tail;
                //å¦‚æœå°¾å·´æ²¡äº†ï¼Œtail = -1 tail-içš„ç»å¯¹å€¼æ°¸è¿œå¤§äºhead-içš„ç»å¯¹å€¼,æ°¸è¿œæ˜¯head-i
                tail = s.indexOf(c, tail + 1);
            }
        }
        return result;
    }
}
