class CustomStack {
    int[] stack;
    int iPoint;

    public CustomStack(int maxSize) {
        stack = new int[maxSize];
        iPoint = -1;
    }

    public void push(int x) {
        //å¦‚æœåˆ°äº†æœ€å
        if (iPoint == stack.length-1) {
            return;
        }
        //å‘å³åç§»ä¸€ä½
        iPoint++;
        //å­˜å€¼
        stack[iPoint] = x;
    }

    public int pop() {
        //æ ˆåº•
        if (iPoint == -1) {
            return iPoint;
        }
        --iPoint;
        //ç›´æ¥è¿”å›å³å¯ï¼Œåé¢ç›´æ¥è¦†ç›–
        return stack[iPoint + 1];
    }

    public void increment(int k, int val) {
        //å½±å“æ•°é‡
        int num = Math.min(k, iPoint + 1);
        //ä¿®æ”¹å€¼
        for (int i = 0; i < num; ++i) {
            stack[i] += val;
        }
    }
}
