class Solution:
    def shortestToChar(self, s: str, c: str) -> List[int]:
        result = [99999 for i in s]
        n = len(s)
        for i in range(n):
            if s[i] != c:
                continue
            result[i] = 0
            for j in range(i-1, -1, -1):
                if s[j] == c:
                    result[j] = 0
                    break
                result[j] = min(abs(i-j), result[j])
            if i == n-1:
                break
            for j in range(i+1, n):
                if s[j] == c:
                    result[j] = 0
                    break
                result[j] = min(abs(i-j), result[j])
        return result
