        if (s[l]===c && s[r]===c)
            array_abs=Math.min(Math.abs(l-pos),Math.abs(r-pos));
        else if (s[l]===c && s[r]!==c)
            array_abs=Math.min(Math.abs(l-pos));
        else
            array_abs=Math.min(Math.abs(r-pos));
        res.push(array_abs);
    }
    return res;
};
