    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> result;
        for(int i = num.size() - 1; i >= 0; i--)
        {
            int curNumber = num[i] + k % 10;
            k /= 10;
            if(curNumber >= 10)
            {
                curNumber -= 10;
                k += 1;
            }
            result.push_back(curNumber);
        }

        for (; k > 0; k /= 10) {
            result.push_back(k % 10);
        }
        reverse(result.begin(), result.end());
        return result;
    }
};
