class Solution:
    def decodeString(self, s: str) -> str:
        stk = []
        stemp = []
        ntemp = []
        for si in s:
            if si != ']':
                stk.append(si)
            else:
                while True:
                    # get letters in []
                    if stk[-1] == '[':
                        # remove [
                        stk.pop()
                        break
                    else:
                        # append letters
                        stemp.append(stk.pop())
                while True:
                    # get numbers before [
                    if stk:
                        if not stk[-1].isnumeric():
                            break
                        else:
                            # append numbers
                            ntemp.append(stk.pop())
                    else:
                        break
                stk.extend(list(reversed(stemp)) * int(''.join(reversed(ntemp))))
                stemp = []
                ntemp = []
        return ''.join(stk)
```
