```java
/*
Bruteforce:
simulate with an array


Optimization:
prefix sum

when pop, adjust the inc for the previous cell

inc list and stack list

Time: O(1) 
Space: O(1) per element

*/

class CustomStack {
	int capacity;
	int size;
	List<Integer> stack = new ArrayList<>();
	List<Integer> inc = new ArrayList<>();

	public CustomStack(int maxSize) {
		capacity = maxSize;
		size = 0;
	}

	public void push(int x) {
		if (size < capacity) {
			stack.add(x);
			inc.add(0);
			size++;
		}
	}

	public int pop() {
		if (size == 0) return -1; // empty check
		int ans = stack.get(size - 1) + inc.get(size - 1);
		if (size >= 2) {
			inc.set(size - 2, inc.get(size - 2) + inc.get(size - 1));
		}
		inc.remove(size - 1);
		stack.remove(size - 1);
		size--;
		return ans;
	}

    // index - > inc at that index
	public void increment(int k, int val) {
		if (size > 0) { 
			inc.set(Math.min(k, size) - 1, inc.get(Math.min(k, size) - 1) + val);
		}
	}

}

