class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        List<Integer> sum = new ArrayList<Integer>();
        int length = num.length;
        num[length - 1] += k;
        for (int i = length - 1; i > 0; i--) {
            int curNum = num[i];
            if (curNum > 9) {
                num[i - 1] += curNum / 10;
                num[i] %= 10;
            }
            sum.add(num[i]);
        }
        do {
            sum.add(num[0] % 10);
            num[0] /= 10;
        } while (num[0] > 0);
        Collections.reverse(sum);
        return sum;
    }
}

