class Solution {
public:
    vector<int> addToArrayForm(vector<int>& A, int K) {

        vector<int> res;
        res.reserve(A.size() + 5);

        int i = A.size() - 1;
        int carry = 0;

        while (i >= 0 || K > 0)
        {
            int n1 = i >= 0 ? A.at(i) : 0;
            int n2 = K > 0 ? K % 10 : 0;
            int sum = n1 + n2 + carry;
            carry = sum / 10;
            res.push_back(sum % 10);
            --i;
            K /= 10;
        }

        if (carry)
        {
            res.push_back(1);
        }

        reverse(res.begin(), res.end());

        return res;
    }
};

