class Solution {
    public:
    string decodeString(string s) {
        std::stack<int> multiStack;
        std::stack<string> stringStack;
        string resultStr;
        int curMulti = 0;

        for (int i = 0; i < s.size(); i++) {
            char c = s[i];
            if(c == '[') {
                multiStack.push(curMulti);
                stringStack.push(resultStr);
                curMulti = 0;
                resultStr.clear();
            }
            else if(c == ']') {
                int tmp = multiStack.top();
                multiStack.pop();
                string tmpStr;
                for( int i = 0; i < tmp; i++ ) {
                    tmpStr.append(resultStr);
                }
                resultStr = (stringStack.top() +tmpStr);
                stringStack.pop();

            }
            else if (c >='0' && c <= '9') {
                curMulti = curMulti* 10 + (c-'0');
            }
            else {
                resultStr.append({c});
            }
        }
        return resultStr;
    }
};
