class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        vector<int> result;
        int n = num.size();

        // ä»ä¸ªä½å¼€å§‹è·Ÿkçš„ä¸ªä½ç›¸åŠ ï¼Œå¤§äº10åˆ™è¿›ä¸€ä½
        for (int i = n - 1; i >= 0; --i) {
            int sum = num[i] + k % 10;
            k /= 10;
            if (sum >= 10) {
                k++;
                sum -= 10;
            }
            result.push_back(sum);
        }

        // æœ€åå‰©ä¸‹çš„kï¼Œ è¯´æ˜kæ˜¯æ¯”åŸæ•°ç»„å¾ˆå¤§å¾ˆå¤§ï¼Œåº”è¯¥æ˜¯æ”¾åœ¨åŠ å’Œåçš„é«˜ä½
        for (; k > 0; k /= 10) {
            result.push_back(k % 10);
        }

        std::reverse(result.begin(), result.end());

        return result;
    }
};

