class Solution {
public:
    string decodeString(string s) {
        std::stack<int>  multiStack;   // 
        std::stack<string> strignStack;
        string resultStr;
        int curMulti = 0;

        for (int i = 0; i < s.length(); i++) {
            char c = s[i];
            if (c == '[') { 
                // å¦‚æœæ˜¯â€œ[â€, å…ˆå­˜æ”¾å½“å‰è®°å½•çš„æ¬¡æ•°å’Œå­—ç¬¦ä¸²å…¥è¾…åŠ©æ ˆ, å†æ¸…ç©ºä¸¤è€…çš„å€¼
                multiStack.push(curMulti);
                strignStack.push(resultStr);
                curMulti = 0;
                resultStr.clear();
            } else if (c == ']') {
                // å–å‡ºæ¬¡æ•°ï¼Œè¯´æ˜éœ€è¦é‡å¤æ ˆé¡¶è®°å½•æ¬¡æ•°çš„resultStr,ä¹Ÿå°±æ˜¯è¿™ä¸ªæ•°å­—åè·Ÿç€çš„[]ä¹‹é—´çš„å­—ç¬¦
                int tmp = multiStack.top();
                multiStack.pop();
                string tmpStr;
                for (int i = 0; i < tmp; i++) {
                    tmpStr.append(resultStr);
                }
                resultStr = (strignStack.top() + tmpStr);
                strignStack.pop();
            } else if (c >= '0' && c <= '9') {
                // æ•°å­—å¯èƒ½å‡ å å‡ ç™¾
                curMulti = curMulti * 10 + (c - '0');
            } else {
                resultStr.append({c});
            }
        }

        return resultStr;
    }
};
```

