class MyQueue {
    private Stack<Integer> s1 = new Stack<>();
    private Stack<Integer> s2 = new Stack<>();
    private int front;

    public MyQueue() {

    }

    // æ¯æ¬¡S1ç©ºéƒ½æ›´æ–°front
    public void push(int x) {
        if (s1.empty())
            front = x;
        s1.push(x);
    }

    public int pop() {
        if (s2.empty()) {
            // s1æ¬ç©º ä¸‹æ¬¡pushæ›´æ–°front
            while(!s1.empty())
                s2.push(s1.pop());
        }
        return s2.pop();
    }

    // peekæ˜¯ä¸¤éƒ¨åˆ†é€»è¾‘ S2ç›¸å½“äºé˜Ÿåˆ—å¦‚ä¸ä¸ºç©ºç›´æ¥è¿”å›peek å¦åˆ™S1æœ€åº•å°±æ˜¯peekå€¼
    public int peek() {
        if (!s2.empty()) 
            return s2.peek();
        return front;
    }

    public boolean empty() {
        return s1.empty() && s2.empty();
    }
}
