var addToArrayForm = function(num, k) {
    let res = [];
    for(let i = num.length - 1; i >= 0; i--){
        let sum = num[i] + k % 10;
        k = Math.floor(k / 10);
        if(sum >= 10){
            sum -= 10;
            k += 1;
        }
        res.push(sum);
    }
    //è§£å†³kä½æ•°å¤§äºæ•°ç»„é•¿åº¦
    while(k > 0){
        res.push(k % 10);
        k = Math.floor(k / 10);
    }
    res.reverse();
    return res;
};
