```cpp
class Solution {
public:
    vector<int> addToArrayForm(vector<int>& num, int k) {
        int i = num.size() - 1;
        while(k > 0){
            num[i] += k;
            k = num[i] / 10;
            num[i--] %= 10;
            if(i < 0 && k > 0){
                num.insert(num.begin(), 0);
                i = 0;
            }
        }
        return num;
    }
