class CustomStack {
    int[] stack;
    int top;
    public CustomStack(int maxSize) {
        stack = new int[maxSize];
        top = -1;
    }

    public void push(int x) {
        if(top < stack.length - 1){
            stack[++top] = x;
        }
    }

    public int pop() {
        if(top > -1){
            int res = stack[top];
            stack[top--] = 0;
            return res;
        }else{
            return -1;
        }
    }

    public void increment(int k, int val) {
        if(top < k - 1){
            k = top + 1;
        }
        for(int i = 0; i < k; i++){
            stack[i] += val;
        }
    }
}
