class CustomStack {
    int[] stack;
    int top;
    public CustomStack(int maxSize) {
        stack = new int[maxSize];
        top = -1;
    }

    public void push(int x) {
        if(top<stack.length-1){
            top++;
            stack[top] = x;
        }
    }

    public int pop() {
        if(top==-1){
            return -1;
        }else{
            return stack[top--];
        }
    }

    public void increment(int k, int val) {
        for(int i=0;i<k;i++){
            if(i<=top){
                stack[i] += val;
            }else{
                break;
            }
        }
    }
}

