class Solution {
    public String decodeString(String s) {

        // ä½¿ç”¨ä¸¤ä¸ªæ ˆ åˆ†åˆ«ç”¨æ¥è®°å½• æ•°å­— å’Œ å…¶ä»–æ‰€æœ‰å­—ç¬¦
        Stack<Integer> stack_num = new Stack<>();
        Stack<String> stack_string = new Stack<>();

        char[] chars = s.toCharArray();
        for(char ch: chars){
            // å¦‚é‡åˆ°æ•°å­— è¿›å…¥æ•°å­—æ ˆ
            if (Character.isDigit(ch))
                stack_num.push(ch - '0');

            // å¦‚é‡åˆ°å­—æ¯ è¿›å…¥å…¶ä»–æ ˆ
            else if (Character.isAlphabetic(ch))
                stack_string.push(String.valueOf(ch));

            // å¦‚é‡åˆ° [ è¿›å…¥å…¶ä»–æ ˆ å¹¶åœ¨æ•°å­—æ ˆåšæ ‡è®°
            else if (ch == '['){
                stack_num.push(-1);
                stack_string.push(String.valueOf("["));
            }

            // é‡åˆ° ] å¼€å§‹å‡ºæ ˆè¿›è¡Œæ‹¼æ¥ åå…¥æ ˆ
            else{

                // æ–°å»ºä¸€ä¸ª StringBuilder æ¥è®°å½•ä¸€æ¬¡è§£ç 
                StringBuilder tmp = new StringBuilder();

                // å½“æ ˆä¸ä¸ºç©ºä¸” æ²¡æœ‰åˆ°æœ¬æ¬¡è§£ç çš„å¤´ å³ å·¦æ‹¬å¼§ [ æ—¶ åœ¨ tmp å‰é¢æ’å…¥è¯¥å­—ç¬¦ï¼ˆä¸²ï¼‰ä¿è¯é¡ºåº
                while (!stack_string.isEmpty() && !"[".equals(stack_string.peek()))
                    tmp.insert(0, stack_string.pop());

                // é‡åˆ°å·¦æ‹¬å¼§ å¼¹å‡ºä¸€æ¬¡
                stack_string.pop();

                // åŒæ—¶å¼¹å‡ºæ•°å­—æ ˆçš„ æ ‡è®° -1
                stack_num.pop();

                // cnt ç”¨äºè®¡æ•°æ‹¬å¼§å‰çš„æ•°å­—æ¥åˆ¤æ–­æ‹¬å¼§é‡Œé¢çš„å­—ç¬¦ä¸²é‡å¤å‡ æ¬¡
                int cnt = 0;
                StringBuilder cntString = new StringBuilder();

                // æ±‚å‡ºé‡å¤æ¬¡æ•°
                while (!stack_num.isEmpty() && -1 != stack_num.peek())
                    cntString.insert(0, stack_num.pop());
                cnt = Integer.parseInt(cntString.toString());

                // æ ¹æ® cnt æ¬¡æ•°æ¥é‡å¤å­—ç¬¦ä¸²
                StringBuilder pushBack = new StringBuilder();
                while(cnt-- > 0)
                    pushBack.append(tmp);

                // é‡å¤ä»¥åå†å‹å…¥æ ˆ
                stack_string.push(pushBack.toString());
            }
        }

        // å½“å­—ç¬¦ä¸²éå†ç»“æŸä»¥åå†ç”¨æ’å…¥çš„æ–¹å¼æ±‡æ€»
        StringBuilder sb = new StringBuilder();
        while(!stack_string.isEmpty())
            sb.insert(0, stack_string.pop());
        return sb.toString();
    }
}

