var addToArrayForm = function(num, k) {
    const numArr = num.reverse();
    const kArr = String(k).split('').reverse().map(Number);

    let result = [];
    let i = 0;
    let curr = 0;
    let carry = 0;
    while(i < numArr.length || i < kArr.length || carry > 0) {
