var addToArrayForm = function (num, k) {
    let k_array = [];
    let length_cal = 0;
    let cal_array = [];
    while (k > 0) {
        let yu = k % 10;
        k_array.unshift(yu);
        k = (k - yu) / 10;
    }

    if (k_array.length < num.length) {
        length_cal = num.length;
        const len = k_array.length
        for (let index = 0; index < length_cal - len; index++) {
            k_array.unshift(0);
        }
    } else {
        length_cal = k_array.length;
        const len = num.length
        for (let index = 0; index < length_cal - len; index++) {
            num.unshift(0);
        }
    }

    for (let index = 0; index < length_cal; index++) {
        const m = num.pop();
        const n = k_array.pop();
        if (m + n >= 10) {
            cal_array.unshift((m + n) % 10);
            k_array[k_array.length - 1] = k_array[k_array.length - 1] + 1;
            if (index === length_cal - 1) {
                cal_array.unshift(1);
            }
        } else {
            cal_array.unshift(m + n)
        }
    }

    return cal_array;
};

