```JavaScript
var CustomStack = function (maxSize) {

    this.stack = [];
    this.maxSize = maxSize;

    //for loop create array, value default null, size = maxSize
    for (let index = 0; index < maxSize; index++) {
        this.stack[index] = null;
    }

};

/** 
 * @param {number} x
 * @return {void}
 */
CustomStack.prototype.push = function (x) {

    // stack's last one is not null, do not push
    if (this.stack[this.maxSize - 1] !== null) {
        return;
    }

    // for loop find the first index is null, push parameter at index
    const indexCanPush = this.stack.findIndex(element => element === null);
    this.stack[indexCanPush] = x;
};

/**
 * @return {number}
 */
CustomStack.prototype.pop = function () {
    // find the first one (called lastOne) is not null from the tail of stack
    const { lastOne, lastOneIndex } = this.lastOne();

    // the index value of lastOne set to null, return last one
    if (lastOne !== -1) {
        this.stack[lastOneIndex] = null
    }

    return lastOne;
};

/** 
 * @param {number} k 
 * @param {number} val
 * @return {void}
 */
CustomStack.prototype.increment = function (k, val) {

    // from tail to head, find the first one is not null
    const { lastOne, lastOneIndex } = this.lastOne();

    for (let index = lastOneIndex; index >= 0 && k > 0; index--, k--) {
        this.stack[lastOneIndex] += val;
    }
};

CustomStack.prototype.lastOne = function () {

    // from tail to head, find the first one is not null
    let lastOne = -1;
    let lastOneIndex = -1;
    for (let tail = this.maxSize - 1; tail >= 0; tail--) {
        if (this.stack[tail] !== null) {
            lastOne = this.stack[tail];
            lastOneIndex = tail;
            return { lastOne, lastOneIndex };
        }
    }

    return { lastOne, lastOneIndex };

};
