func shortestToChar(s string, c byte) []int {
	var cIdx []int
	var ret []int

	initCIdx := func() {
		for i := 0; i < len(s); i++ {
			if s[i] == c {
				cIdx = append(cIdx, i)
			}
		}
	}

	getDistance := func(idx int) int {
		min := math.MaxInt32
		for _, ii := range cIdx {
			tmp := int(math.Abs(float64(ii - idx)))
			if tmp < min {
				min = tmp
			}
		}
		return min
	}

	initCIdx()
	for i := 0; i < len(s); i++ {
		ret = append(ret, getDistance(i))
	}

	return ret
}

