func decodeString(s string) string {
	var ret bytes.Buffer
	var cache []byte

	findRepeatNumString := func() (int, int, int, int) {
		numBegin, numEnd, sBegin, sEnd  := -1, -1, -1, -1

		sEnd = len(cache) - 1
		for j := sEnd; j >=0; j-- {
			if numEnd == -1 && cache[j] >= '0' && cache[j] <= '9' {
				sBegin = j + 2
				numEnd = j + 1
				continue
			}

			if numEnd != -1 && (cache[j] < '0' || cache[j] > '9') {
				numBegin = j + 1
				break
			}
		}

		if numBegin == -1 {
			numBegin = 0
		}

		return numBegin, numEnd, sBegin, sEnd
	}

	decodeSubString := func() {
		var subString bytes.Buffer

		numBegin, numEnd, sBegin, sEnd := findRepeatNumString()
		num, _ := strconv.Atoi(string(cache[numBegin:numEnd]))
		for i := 0; i < num; i++ {
			subString.Write(cache[sBegin:sEnd])
		}

		if numBegin == 0 {
			ret.Write(subString.Bytes())
			cache = cache[:0]
		} else {
			cache = append(cache[:numBegin], subString.Bytes()...)
		}
	}

	for i := 0; i < len(s); i++ {
		if len(cache) == 0 && s[i] >= 'a' && s[i] <= 'z' {
			ret.WriteByte(s[i])
			continue
		}

		cache = append(cache, s[i])
		if s[i] == ']' { decodeSubString() }
	}

	return ret.String()
}


