class Solution {
    public int[] shortestToChar(String s, char c) {
            char[] array = s.toCharArray();
            int len = s.length();
            int[] res = new int[len];
            Arrays.fill(res, len);
            int idx_c = -1;
            for(int i = 0; i < len ; i++) {
                if(array[i] == c) {
                    idx_c = i;
                    res[i] = 0;
                    continue;
                }
                if(idx_c != -1) {
                    res[i] = i - idx_c;
                }
            }
            for(int i = len - 1; i >= 0 ; i--) {
                if(array[i] == c) {
                    idx_c = i;
                    continue;
                }
                if(idx_c > i) {
                    res[i] = Math.min(res[i], idx_c - i);
                }

            }
            return res;
    }
}

class CustomStack {

    int[] data;
    int[] add;
    int maxSize;
    int curSize;

    public CustomStack(int maxSize) {
        data = new int[maxSize];
        add = new int[maxSize];
        this.maxSize = maxSize;
        this.curSize = 0;
    }

    public void push(int x) {
        if(curSize == maxSize){
            return;
        }
        data[curSize++] = x;
    }

    public int pop() {
        if(curSize == 0) return -1;
        int res = data[curSize - 1] + add[curSize - 1];
        if(curSize > 1) {
            add[curSize - 2] += add[curSize - 1]; 
        }
        add[curSize - 1] = 0;
        curSize--;
        return res;
    }

    public void increment(int k, int val) {
        if(curSize == 0) return;
        add[Math.min(k, curSize) - 1] += val;
    }
}

/**
 * Your CustomStack object will be instantiated and called as such:
 * CustomStack obj = new CustomStack(maxSize);
 * obj.push(x);
 * int param_2 = obj.pop();
 * obj.increment(k,val);
 */

class Solution {
    public String decodeString(String s) {
        // ç¢°åˆ°ã€ å…¥æ ˆå­—æ¯  ç¢°åˆ° ã€‘å‡ºæ ˆå­—æ¯å¹¶è®¡ç®—
        Deque<String> cStack = new LinkedList<>();   // å­˜å‚¨çš„æ˜¯[ å  æ•°å­—å‰çš„å­—ç¬¦ä¸²  å¦‚  2[ab3[cd4[ef]]] ä¸­çš„ab  cd  ä¾æ¬¡å…¥æ ˆ  
        Deque<Integer> nStack = new LinkedList<>();  // å­˜å‚¨çš„æ˜¯[ å‰çš„æ•°å­—  å¦‚ä¸Šä¾‹ä¸­çš„ 2 3 4
        char[] arr = s.toCharArray();
        // éš¾ç‚¹  æ•°å­—å¯èƒ½ä¸æ­¢ä¸€ä½æ•°
        int num = 0;
        String piece = "";
        for(char c : arr) {
            if ( c >= '0' && c <= '9') {
                num = num * 10 + (c - '0');
            } else if (c == '[') {
                nStack.push(num);
                cStack.push(piece);
                num = 0;
                piece = "";
            } else if (c == ']') {
                String inner = piece;
                int cnt = nStack.pop() - 1;
                for(int i = 0 ; i < cnt; i ++) {
                    piece += inner;
                }
                piece = cStack.pop() + piece;
            } else {
                piece += c;
            }
        }
        return piece;
    }
