class Solution {
public:
#define MIN(x,y) ((x)<(y) ? (x) : (y))
    vector<int> shortestToChar(string str, char c) {
        vector<int> flags;
        vector<int> answer;
        for(int i=0;i<str.size();i++){
            if(str[i]==c) flags.push_back(i);
        }
        //print_vector(flags);
        //cout<<"end\n";
        int current_flag=0;
        for(int i=0;i<str.size();i++){
            if(current_flag==flags.size()){
            answer.push_back(i-flags[current_flag-1]);
            continue;
            }
            if(i>flags[current_flag]) {current_flag++; i--; continue;}
            else if(current_flag==0){
            answer.push_back(flags[current_flag]-i);
            continue;
            }
            answer.push_back(MIN(i-flags[current_flag-1],flags[current_flag]-i));
        }
        return answer;
            }
};


