class CustomStack {
public:
    CustomStack(int maxSize) 
    {
        max=maxSize;
        arr=new int[maxSize];
        cur=0;
    }
    
    void push(int x) 
    {
        if(cur>=max) return;
        arr[cur]=x;
        cur++;
    }
    
    int pop() 
    {
        if(cur==0) return -1;
        cur--;
        return arr[cur];
    }
    
    void increment(int k, int val) 
    {
        int count=0;
        if(cur<=k-1)
            count=cur;
        else
            count=k;
        
        for(int i=0;i<count;i++)
            arr[i]+=val;
    }
public:
    int* arr;
    int cur;
    int max;
};
