var decodeString = function(s) {
    let stack = [];
    for (let char of s) {
        if (char != ']') {
            stack.push(char);
        } else {
            let repeatStr = stack.pop();
            let tmpC = stack.pop();
            while (tmpC != '[') {
               repeatStr = tmpC + repeatStr;
               tmpC = stack.pop();
            }
            let repeatTime = "";
            while (stack.length && !isNaN(stack.slice(-1))) {
                repeatTime = stack.pop() + repeatTime;
            }
            stack.push(repeatStr.repeat(+repeatTime));
        }
    }
    return stack.join("");
};
