var decodeString = function(s){
    const stack = [];

    for (let char of s) {
        if (char !== ']') {
            stack.push(char);
            continue;
        }

        let cur = stack.pop();
        let str = '';
        while (cur !== '[') {
            str = cur + str;
            cur = stack.pop();
        }

        // now get the number here
        cur = stack.pop();
        let num = '';
        // be careful here, how we count the number
        while (cur >= '0' && cur <= '9') {
            num = cur + num;
            cur = stack.pop();
        }
        stack.push(cur);
        stack.push(str.repeat(num));
    }
    return stack.join('');
}
