func decodeString(s string) (res string) {
	var stack []string
	getDigits := func(ptr *int) (ret string) {
		for ; s[*ptr] >= '0' && s[*ptr] <= '9'; *ptr++ {
			ret += string(s[*ptr])
		}
		return
	}
	getStrings := func(strs []string) (ret string) {
		for _, str := range strs {
			ret += str
		}
		return
	}
	ptr := 0
	for ptr < len(s) {
		chr := s[ptr]
		if chr >= '0' && chr <= '9' {
			digits := getDigits(&ptr)
			stack = append(stack, digits)
		} else if chr >= 'a' && chr <= 'z' || chr == '[' {
			stack = append(stack, string(chr))
			ptr++
		} else {
			ptr++
			var sub []string
			for stack[len(stack)-1] != "[" {
				sub = append(sub, stack[len(stack)-1])
				stack = stack[:len(stack)-1]
			}
			n := len(sub)
			for i := 0; i < n/2; i++ {
				sub[i], sub[n-i-1] = sub[n-i-1], sub[i]
			}
			stack = stack[:len(stack)-1]
			repTimes, _ := strconv.Atoi(stack[len(stack)-1])
			stack = stack[:len(stack)-1]
			st := strings.Repeat(getStrings(sub), repTimes)
			stack =append(stack, st)
		}
	}
	res = getStrings(stack)
	return
}
