public String decodeString(String s) {
        Stack<Character> stack = new Stack<>();
        for(char c : s.toCharArray()){
            if(c != ']'){
                stack.push(c);
            }else{
                StringBuilder sb = new StringBuilder();
                while(!stack.isEmpty() && stack.peek()!='['){
                    sb.insert(0,stack.pop());
                }
                String sub = sb.toString();//one of the subStr need to be multiplied
                stack.pop();//should be a [
                sb = new StringBuilder();//clean current sb
                while(!stack.isEmpty() && Character.isDigit(stack.peek())){
                    sb.insert(0, stack.pop());
                }    
                int count = Integer.parseInt(sb.toString());
                while(count != 0){
                    for(char elem: sub.toCharArray()){
                        stack.push(elem);
                    }
                    count--;
                }
            }
        }
        StringBuilder res = new StringBuilder();
        while(!stack.isEmpty()){
            res.insert(0, stack.pop());
        }
        return res.toString();
    }

class MyStack {
    private Queue<Integer> q;
    private int top_elem;

    public MyStack() {
        q = new LinkedList<>();
        top_elem = 0; 
    }

    public void push(int x) {
        q.offer(x);
        top_elem = x;
    }

    public int pop() {
        int size = q.size();
        while(size > 2){
            q.offer(q.poll());
            size--;
        }
        top_elem = q.peek();
        q.offer(q.poll());
        return q.poll();
    }

    public int top() {
        return top_elem;
    }

    public boolean empty() {
        return q.isEmpty();
    }
}

