class Solution {
    public int[] shortestToChar(String s, char c) {
        int len = s.length();
        int[] res = new int[len];
        List<Integer> tmp = new ArrayList<>();
        int index = 0 ;
        for(int i = 0; i < len; i++){
            if(s.charAt(i) == c){
                tmp.add(i);
            }
        }

        for(int i = 0; i < len; i++ ){
            int cur =  tmp.get(index);
            if(index < 1){
                res[i] = Math.abs(i - cur);
            }else{
                res[i] = Math.min(Math.abs(i - cur), Math.abs(i - tmp.get(index - 1)));
            }
            if(i == tmp.get(index) && (index + 1) < tmp.size()){
                index ++ ;
            }
        }
        return res ;

    }
}
