class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
        int n = s.size();
        vector<int> ans(n);
        int prec = INT_MIN / 2;
        
        for (int i = 0; i < n; i++) {
            if (s[i] == c) {
                ans[i] = 0;
                prec = i;
            } else {
                ans[i] = i - prec;
            }
        }

        prec = INT_MAX / 2;
        for (int i = n - 1; i >= 0; i--) {
            if (s[i] == c) {
                s[i] = 0;
                prec = i;
            } else {
                ans[i] = min(ans[i], prec - i);
            }
        }
        return ans;
    }
};
```

