class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        int n =  num.length;
        int cur = k;
        List<Integer> ans = new ArrayList();
        
        int i = n;
        
        while(--i >=0 || cur > 0){
            if( i >= 0){
                cur +=num[i];
            }
            
            ans.add(cur % 10);
            cur /= 10;
        }
        
        
        Collections.reverse(ans);
        return ans;
    }
}

class Solution {
    public int[] shortestToChar(String s, char c) {
        int len = s.length();
        int[] arr = new int[len];
        int cpos = -len;
        
        for(int i = 0; i < len; i++){
            if(s.charAt(i) == c){
                cpos = i;
            }
            
            arr[i] = i - cpos;
        }
        
        for ( int i =len -1; i>=0; i--){
            if(s.charAt(i) == c){
                cpos=i;
            }
            arr[i] = Math.min(arr[i],Math.abs(i-cpos));
        }
        
        return arr;
    }
}

