class Solution {
    public int[] shortestToChar(String s, char c) {
        int n = s.length();
        int pos = -n;
        int[] answer = new int[n];
        
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == c) pos = i;
            answer[i] = i - pos;
