class Solution {
public:
    vector<int> shortestToChar(string s, char c) {
      int n = s.size();
      vector<int> res(n, 0), nextC;
      for (int i = 0; i < n; i++)
        if (s[i] == c) nextC.emplace_back(i);
      int m = nextC.size();
      for (int i = 0, j = 0; i < n; i++) {
        res[i] = abs(nextC[j] - i);
        if (j < m - 1 && nextC[j] == i) j++;
      }
      for (int i = n - 1, j = m - 1; i >= 0; i--) {
        res[i] = min(abs(nextC[j] - i), res[i]);
        if (j > 0 && nextC[j] == i) j--;
      }
      return res;
    }
};
```

