class Solution {
	public int[] shortestToChar(String s, char c) {
		int len = s.length(), prev = s.indexOf(c);
		int[] res = new int[len];
		for (int i = 0; i < len; i++) {
			if (s.charAt(i) == c) prev = i;
			int tmp = i - prev;
			res[i] = tmp >= 0 ? tmp : -tmp;
		}
		prev = s.lastIndexOf(c);
		for (int i = len - 1; i >= 0; i--) {
			if (s.charAt(i) == c) prev = i;
			int tmp = i - prev;
			tmp = tmp >= 0 ? tmp : -tmp;
			res[i] = tmp >= res[i] ? res[i] : tmp;
		}
		return res;
	}
}
