class Solution {
    public List<Integer> addToArrayForm(int[] num, int k) {
        ArrayList<Integer> res = new ArrayList<Integer>();
        int n = num.length - 1;
        int carry = 0;

        while (n >= 0 || k != 0) {
            int a = n < 0 ? 0 : num[n];
            int b = k % 10;

            int temp = (a + b + carry) % 10;
            carry = (a+ b + carry) / 10;
            res.add(temp);
            n--;
            k /= 10;
        }
        if (carry == 1) {
            res.add(1);
        }
        Collections.reverse(res);
        return res;
    }
}
