var addToArrayForm = function(num, k) {
    let a,res=[];
    for(let i = num.length-1; i >= 0; i--)
    {
        a = num[i] + k % 10;
        k = Math.floor(k/10);
        if(a >= 10){
            ++k;
            a = a-10;
        };
        res.push(a);
    }
    while(k!=0){
        res.push(k%10);
        k = Math.floor(k/10);
    }
    
    return res.reverse();
};
